var express =  require("express");
var app = express();
var bodyParser = require("body-parser");

app.use(bodyParser.urlencoded({extended: true}));


app.set("view engine" , "ejs");
var shops =[
		{name: "Vellore Institute Of Technology", image:"data:image/jpeg;base64,"},
		{name: " SRM university", image:"data:image/jpeg;base64,"},
		{name: " Manipal Institute", image:"https://cache.careers360.mobi/media/presets/860X430/article_images/2020/11/18/Manipal-University-Admission-2021.webp"}
		
	];

app.get("/", function(req,res){
	res.render("landing");
});

app.get("/shops", function(req,res){
	
	res.render("shops",{shops:shops});
});

app.post("/shops", function(req,res){
	var name = req.body.name;
	var image = req.body.image;
	var newShop ={name:name , image:image}
	shops.push(newShop);
	res.redirect("/shops");
	
});

app.get("/shops/new",function(req,res){
	res.render("new.ejs");
});
	


app.listen(process.env.PORT, process.env.IP, function(){
	console.log("Chota Cart is about to start");
});



